// CEyeDxTextBlock.h#pragma onceconst int currentCEyeDxTextBlockSchema	= 1;class CEyeDxTextBlock : public CObject{	DECLARE_SERIAL(CEyeDxTextBlock)	// Constructorspublic:	CEyeDxTextBlock();	virtual ~CEyeDxTextBlock();		virtual	CString	GetBlockText() { return m_BlockText; };	virtual void	SetBlockText(CString &theText) { m_BlockText = theText; };	virtual long	GetBlockType() { return m_BlockType; };	virtual void	SetBlockType(long theType) { m_BlockType = theType; };	virtual void Serialize(CArchive& ar);private:	long			m_BlockType;	// One of the "kReportLayout*" constants defined in the globals.h file	CString			m_BlockText;};